package myset.exercise

class MySet {
    var size = 0
    private set
    var setValue:  Array<String?> = arrayOfNulls(10)
    private set
    fun isEmpty(): Boolean {
        return size == 0
    }

    fun add(element: String) {
        setValue[size] = element
        size++
    }

    fun contains(element: String): Boolean {
        return findElement(element) > -1
    }

    fun remove(element: String) {
        val index = findElement(element)
        if (index < 0) {
            return
        }
        setValue[index] = setValue[size - 1]
        setValue[size - 1] = null
        size--
    }

    private fun findElement(element: String): Int {
        for (index in 0 until size) {
            if (setValue[index] == element) {
                return index
            }
        }
        return -1
    }
}
